/* global lib */

pie.ns('lib.views');

lib.views.nav = pie.view.extend('nav', {

  init: function(el) {
    this._super({
      renderOnSetup: true,
      template: 'nav',
      setup: true,
      el: document.body,
    });
  },

  setup: function() {
    app.emitter.on('urlChanged', this.navigationChanged.bind(this));
    this.emitter.on('afterSetup', this.render.bind(this));

    this.on('click', '.nav-toggle', this.toggleNav.bind(this));
    this.on('click', '.page', this.hideNav.bind(this));

    this._super();
  },

  render: function() {
    this.qs('.page-nav').innerHTML = app.templates.render('nav');
  },

  navigationChanged: function() {
    var path = app.parsedUrl.pathWithRoot,
    target = this.qs('.page-nav ul li a[href="' + path + '"]');

    pie.dom.all(this.qsa('.page-nav li.is-active'), 'classList.remove', 'is-active');

    if(target) target.parentNode.classList.add('is-active');
    this.el.classList.remove('nav-active');
  },

  toggleNav: function() {
    this.el.classList.toggle('nav-active');
  },

  hideNav: function() {
    this.el.classList.remove('nav-active');
  }

});



lib.views.page = pie.activeView.extend('page', {
  init: function() {
    this._super({
      renderOnSetup: true
    });
  },

  navigationUpdated: function() {
    this.render();
    window.scrollTo(0,0);
  },

  templateName: function() {
    return app.parsedUrl.data.page || 'getting-started';
  }

});

window.app = new pie.app({
  uiTarget: '.page',
  navigationContainer: 'body',
  routerOptions: {
    root: '/docs/guide'
  }
});

// get a "nav" view in there. this is "outside" of the normal routed application since it's always present.
// alternatively, we could create a "layout" view to manage this and the current subview.
app.emitter.once('beforeStart', function() {
  var nav = new lib.views.nav();
  app.addChild('nav', nav);
});


// set up our page routes.
app.router.map({
  '/' : {view: 'page', name: 'root'},
  '/about' : {view: 'about', name: 'about'},
  '/:page' : {view: 'page', name: 'page'},
  'pageApi' : '/pages/:page.html'
});


app.i18n.load({
  project: 'pie.js',
  ns: 'pie',
  gist: '994b656c26b16d5c2c77'
});

app.helpers.register('gist', function(filename, gistId) {
  gistId = gistId || app.i18n.t('gist');
  return '<x-gist gist="' + gistId + '" filename="' + filename + '"></x-gist>';
});
/* global hljs */

(function() {
  'use strict';

  var gistCache = {};
  var proto = Object.create(HTMLElement.prototype);

  proto.externalResources = function(){
    return [app.router.path('/js/highlight.js')];
  },

  proto.contentCallback = function(data) {
    var filename = this.getAttribute('filename') || Object.keys(data.files)[0],
    file = data.files[filename],
    content = file && pie.string.escapeHtml(file.content) || 'NOT FOUND';

    var lines = content.split("\n");

    // remove empty lines from the beginning
    while(lines.length && !lines[0].trim().length) lines.shift();

    // and from the end
    while(lines.length && !lines[lines.length - 1].trim().length) lines.pop();

    // then remove the block of whitespace from the left.
    if(lines.length) {
      var toStrip = lines[0].match(/^(\s+)/);

      if(toStrip && toStrip.length) {
        toStrip = toStrip[0];
        var regex = new RegExp("^[\\s]{" + toStrip.length + "}");
        lines = lines.map(function(l){ return l.replace(regex, ""); });
      }
    }

    app.resources.load(this.externalResources(), function() {

      this.innerHTML = "<code><pre>" + lines.join("\n") + "</pre></code>";

      hljs.configure({useBr: true, language: file && file.language || ['javascript', 'json', 'html']});
      hljs.highlightBlock(this.querySelector('pre'));

    }.bind(this));
  };

  proto.attachedCallback = function() {
    var gistId = this.getAttribute('gist'),
    path = 'https://api.github.com/gists/' + gistId;

    this.innerHTML = '<code><pre class="hljs">Loading...</pre></code>';
    this.classList.add('gist-loading');

    setTimeout(function(){
      app.resources.load({
        src: path,
        dataSuccess: function(content){
          gistCache[gistId] = content;
        }
      }, function() {
        this.contentCallback(gistCache[gistId]);
        this.classList.remove('gist-loading');
      }.bind(this));
    }.bind(this), 1);
  };

  // register element as x-gist
  window.GistElement = document.registerElement('x-gist', {
    prototype: proto
  });
})();
